"use strict";var login=getCookie("login");login?($(".login").html(login),$(".login").attr("href","")):$(".login").html("登录");var goodsInfo=JSON.parse(localStorage.getItem("goods_info")),str='\n    <section class="pic box">\n        <div class="imgbox">\n            <img src="'.concat(goodsInfo.scr,'" alt="">\n        </div>\n        <nav>\n            <li><img src="').concat(goodsInfo.img1,'" alt=""></li>\n            <li><img src="').concat(goodsInfo.img2,'" alt=""></li>\n            <li><img src="').concat(goodsInfo.img3,'" alt=""></li>\n            <li><img src="').concat(goodsInfo.img4,'" alt=""></li>\n            <li><img src="').concat(goodsInfo.img5,'" alt=""></li>\n        </nav>\n    </section>\n    <section class="word">\n        <p class="title">').concat(goodsInfo.title,'</p>\n        <p class="price"><span>价&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;格</span>￥').concat(goodsInfo.price,'</p>\n        <p class="weight"><span>重&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;量</span>').concat(goodsInfo.weight,'</p>\n        <p class="shop"><span>商&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;店</span>').concat(goodsInfo.shop,'</p>\n        <p class="sell"><span>销&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;售</span>').concat(goodsInfo.sell,'</p>\n        <button class="add_cart">加入购物车</button>\n        <button class="cart">去购物车结算</button>\n    </section>\n    ');$(".detail").html(str);var add=document.querySelector(".add_cart"),info=JSON.parse(localStorage.getItem("goods_info"));add.onclick=function(){var n=JSON.parse(localStorage.getItem("cartList"))||[];if(n.some(function(n){return n.id==info.id})){for(var o=0;o<n.length;o++)if(n[o].id===info.id){n[o].number++;break}}else info.number=1,n.push(info);localStorage.setItem("cartList",JSON.stringify(n)),alert("添加商品成功")};var cart=document.querySelector(".cart");cart.onclick=function(){window.location.href="../pages/cart.html"};